def user_avatar(request):
    resp = dict(
        avatars={
            'john': {
                'title': "John Doe",
                'cover': '/static/images/img.jpg',
            },
        },
    )

    for alias in resp['avatars']:
        resp['avatars'][alias]['alias'] = alias

    resp['profile'] = resp['avatars']['john']

    return resp

